mapper.select:
select uid, id, name, email, isadmin, pwd, image
from user
where id = #{id} and pwd = #{pwd}

select uid, id, name, email, isadmin, image
from user
where id = #{id}

select token
from user
where id = #{id}

mapper.update:
update user
set token = #{token}
where id = #{id}

update user
set token = #{token, jdbcType=VARCHAR}
where id = #{id}

mapper.select:
select count(id)
from user
where id = #{id}

mapper.update:
update user
set name = #{name}, email = #{email}, pwd = #{pwd}, image = #{image}
where uid = #{uid}

mapper.select:
select id 
from user
where email = #{email}

